private async onAudioData(data: AudioDataWithUser): Promise<void> {
        // ... existing code ...

        // Quick transcription for stop commands if we're speaking
        if (this.isSpeaking) {
            try {
                const wavBuffer = await this.convertPcmToWavInMemory(data.samples, data.sampleRate);
                const text = await this.transcriptionService.transcribe(Buffer.from(wavBuffer));

                if (text) {
                    const normalizedText = text.toLowerCase().trim();
                    const isStopCommand = this.stopPhrases.some(phrase => normalizedText.includes(phrase));

                    if (isStopCommand) {
                        elizaLogger.info("[SttTtsPlugin] Stop command detected:", { text });
                        this.shouldStopSpeaking = true;
                        this.ttsQueue = []; // Clear pending speech
                        return;
                    }
                }
            } catch (err) {
                // Ignore errors in stop detection - don't want to affect main flow
                elizaLogger.debug("[SttTtsPlugin] Error checking for stop command:", err);
            }
        }
